public class Magician extends Role {
    // 治療量
    private int healpower;

    // 建構子：使用父類別 Role 的建構子初始化名稱、生命、攻擊力，並設定治療量
    public Magician(String name, int health, int attackPower, int healpower) {
        super(name, health, attackPower);
        this.healpower = healpower;
    }

    // 取得治療量
    public int getHealpower() {
        return healpower;
    }

    // 使用魔法攻擊對手（對其他魔法師）並輸出中文描述
    public void attack(Magician opponent) {
        opponent.setHealth(opponent.getHealth() - this.getAttackPower());
        System.out.println(this.getName() + " 使用魔法攻擊 " + opponent.getName() + "，造成 "
                + this.getAttackPower() + " 點傷害。 (" + opponent.getName() + " 生命剩餘: " + opponent.getHealth() + ")");
    }

    // 治療友軍（劍士）並輸出中文描述
    public void heal(SwordMan ally) {
        ally.setHealth(ally.getHealth() + this.healpower);
        System.out.println(this.getName() + " 治療 " + ally.getName() + "，恢復 " + this.healpower + " 點生命值。 (" + ally.getName() + " 生命: " + ally.getHealth() + ")");
    }

    // 以字串描述 Magician（包含父類資訊與治療量）
    @Override
    public String toString() {
        return super.toString() + ", 治療量: " + healpower;
    }
}

public class Magician extends Role {
    // 治療量（建立後不變）
    private final int healpower;

    // 建構子：使用父類別 Role 的建構子初始化名稱、生命、攻擊力，並設定治療量
    public Magician(String name, int health, int attackPower, int healpower) {
        super(name, health, attackPower);
        this.healpower = healpower;
    }

    // 取得治療量
    public int getHealpower() {
        return healpower;
    }

    // 使用魔法攻擊對手：接受 Role 以支援不同類型的目標
    public void attack(Role opponent) {
        opponent.setHealth(opponent.getHealth() - this.getAttackPower());
        System.out.println(this.getName() + " 使用魔法攻擊 " + opponent.getName() + "，造成 "
                + this.getAttackPower() + " 點傷害。 (" + opponent.getName() + " 生命剩餘: " + opponent.getHealth() + ")");
    }

    // 治療友軍：接受 Role 以支援不同類型的目標
    public void heal(Role ally) {
        ally.setHealth(ally.getHealth() + this.healpower);
        System.out.println(this.getName() + " 治療 " + ally.getName() + "，恢復 " + this.healpower + " 點生命值。 (" + ally.getName() + " 生命: " + ally.getHealth() + ")");
    }

    // 以字串描述 Magician（包含父類資訊與治療量）
    @Override
    public String toString() {
        return super.toString() + ", 治療量: " + healpower;
    }
}

// src/Main.java
// 範例主程式：示範 SwordMan 與 Magician 的互動，移除未使用的警告
public class Main {
    public static void main(String[] args) {
        // 建立角色
        SwordMan swordsman1 = new SwordMan("艾倫(好賤)", 100, 15);
        SwordMan swordsman2 = new SwordMan("布萊恩(超賤)", 80, 100);
        Magician magician1 = new Magician("梅林(好法)", 70, 10, 20);
        Magician magician2 = new Magician("莫甘娜(超法)", 60, 12, 18);

        System.out.println("戰鬥開始：");

        // 劍士互相攻擊
        swordsman1.attack(swordsman2);
        swordsman2.attack(swordsman1);

        // 魔法師互相攻擊
        magician1.attack(magician2);
        magician2.attack(magician1);

        // 魔法師治療劍士
        magician1.heal(swordsman1);

        // 顯示角色狀態

    }
}

// src/Main.java
// 範例主程式：示範 SwordMan 與 Magician 的互動，移除未使用的警告
public class Main {
    public static void main(String[] args) {
        // 建立角色
        SwordMan swordsman1 = new SwordMan("艾倫(好賤)", 100, 15);
        SwordMan swordsman2 = new SwordMan("布萊恩(超賤)", 80, 100);
        Magician magician1 = new Magician("梅林(好法)", 70, 10, 20);
        Magician magician2 = new Magician("莫甘娜(超法)", 60, 12, 18);

        System.out.println("戰鬥開始：");

        // 劍士互相攻擊
        swordsman1.attack(swordsman2);
        swordsman2.attack(swordsman1);

        // 魔法師互相攻擊
        magician1.attack(magician2);
        magician2.attack(magician1);

        // 魔法師治療劍士
        magician1.heal(swordsman2);

        // 顯示角色狀態（並顯示魔法師的治療量，使用 getHealpower()）
        System.out.println(swordsman1.getName() + " 生命: " + swordsman1.getHealth() + "，存活: " + swordsman1.isAlive());
        System.out.println(swordsman2.getName() + " 生命: " + swordsman2.getHealth() + "，存活: " + swordsman2.isAlive());
        System.out.println(magician1.getName() + " 生命: " + magician1.getHealth() + "，存活: " + magician1.isAlive() + "，治療量: " + magician1.getHealpower());
        System.out.println(magician2.getName() + " 生命: " + magician2.getHealth() + "，存活: " + magician2.isAlive() + "，治療量: " + magician2.getHealpower());
    }
}

public class SwordMan extends Role
{

    // 建構子：初始化名稱、生命與攻擊力
    public SwordMan(String name, int health, int attackPower) {
        super(name, health, attackPower);
    }


    // 攻擊對手：對對手造成傷害並輸出中文訊息
    public void attack(SwordMan opponent) {
        opponent.setHealth(opponent.getHealth() - this.getAttackPower());
        System.out.println(this.getName() + " 攻擊 " + opponent.getName() + "，造成 "
                + this.getAttackPower() + " 點傷害。" + opponent);
    }

}

public class SwordMan extends Role
{

    // 建構子：初始化名稱、生命與攻擊力
    public SwordMan(String name, int health, int attackPower) {
        super(name, health, attackPower);
    }


    // 攻擊對手：接受 Role 以支援不同類型的目標，對目標造成傷害並輸出中文訊息
    public void attack(Role opponent) {
        opponent.setHealth(opponent.getHealth() - this.getAttackPower());
        System.out.println(this.getName() + " 攻擊 " + opponent.getName() + "，造成 "
                + this.getAttackPower() + " 點傷害。 (" + opponent.getName() + " 生命剩餘: " + opponent.getHealth() + ")");
    }

}
