public class RPG {
    public static void main(String[] args) {
        // 建立劍士和魔法師角色
        SwordsMan swordsMan_light = new SwordsMan("光明劍士", 100, 20);
        SwordsMan swordsMan_dark = new SwordsMan("黑暗劍士", 100, 25);

        Magician magician_light = new Magician("光明法師", 80, 15, 10);
        Magician magician_dark = new Magician("黑暗法師", 80, 20, 5);

        ShieldSowrdsMan shieldSowrdsMan = new ShieldSowrdsMan("盾牌劍士", 120, 18);

        Role[] gameRoles = {swordsMan_light, swordsMan_dark, magician_light, magician_dark, shieldSowrdsMan};

        // 戰鬥過程
        System.out.println("戰鬥開始！");
        for (Role currentRole : gameRoles)
        {
            if (!currentRole.isAlive())
            {
                continue;
            }
            if (currentRole instanceof SwordsMan)
            {
                currentRole.attack(gameRoles[(int) (Math.random() * gameRoles.length)]);
            }
            else if (currentRole instanceof Magician) {
                Magician magician = (Magician) currentRole;

                if (Math.random() < 0.5) {
                    // 攻擊隨機對手
                    magician.attack(gameRoles[(int) (Math.random() * gameRoles.length)]);
                } else {
                    // 治療自己
                    magician.heal(gameRoles[(int) (Math.random() * gameRoles.length)]);
                }
            }
        }
    }
}

public class RPG {
    public static void main(String[] args) {
        // 建立劍士和魔法師角色
        SwordsMan swordsMan_light = new SwordsMan("光明劍士", 100, 20);
        SwordsMan swordsMan_dark = new SwordsMan("黑暗劍士", 100, 25);

        Magician magician_light = new Magician("光明法師", 80, 15, 10);
        Magician magician_dark = new Magician("黑暗法師", 80, 20, 5);

        ShieldSowrdsMan shieldSowrdsMan = new ShieldSowrdsMan("盾牌劍士", 120, 18);

        Role[] gameRoles = {swordsMan_light, swordsMan_dark, magician_light, magician_dark, shieldSowrdsMan};

        // 戰鬥過程
        System.out.println("戰鬥開始！");
        for (Role currentRole : gameRoles)
        {
            if (!currentRole.isAlive())
            {
                continue;
            }
            if (currentRole instanceof SwordsMan)
            {
                currentRole.attack(gameRoles[(int) (Math.random() * gameRoles.length)]);
            }
            else if (currentRole instanceof Magician) {
                Magician magician = (Magician) currentRole;

                if (Math.random() < 0.5) {
                    // 攻擊隨機對手
                    magician.attack(gameRoles[(int) (Math.random() * gameRoles.length)]);
                } else {
                    // 治療自己
                    magician.heal(gameRoles[(int) (Math.random() * gameRoles.length)]);
                }
            }
        }
    }
}

public class ShieldSowrdsMan extends SwordsMan{

    private int defensecapacity;

        // 建構子：初始化劍士的名稱、生命值和攻擊力

        public ShieldSowrdsMan(String name, int health, int attackPower) {
            super(name, health, attackPower);
            this.defensecapacity = defensecapacity;
        }
        @Override
        public void attack(Role opponent) {
            int reducedDamage = this.getAttackPower() - 5; // 減少5點傷害
            opponent.setHealth(opponent.getHealth() - reducedDamage);
            System.out.println(this.getName() + " 使用盾牌攻擊 " + opponent.getName() + " 造成 " +
                    reducedDamage + " 點傷害。" + opponent);
        }

        public int getDefensecapacity()
        {
            return defensecapacity;
    }
public void defence()
{
    this.setHealth(this.getHealth() + defensecapacity);
    System.out.println(this.getName() + " 使用盾牌防禦，回復 " + defensecapacity + " 點生命值。" + this);

}
}

public class ShieldSowrdsMan extends SwordsMan{

    private int defensecapacity;

        // 建構子：初始化劍士的名稱、生命值和攻擊力

